import React, { useState, useEffect } from 'react';

import {
  getSomething
  //getLinks
  //createTags
  //getTags
  //countClicks
} from '../api';

/*
import {
  App,
  form
} from './components';
*/

const App = () => {
  const [message, setMessage] = useState('');

  useEffect(() => {
    getSomething()
      .then(response => {
        setMessage(response.message);
      })
      .catch(error => {
        setMessage(error.message);
      });
  });

  return (
    <div className="App">
      <h1>The Great Linkerator</h1>
      <h2>{ message }</h2>
    </div>
  );
}

export default App;
